<form action="dinata" method="post" id="form_chat" onsubmit="return addChatS(this)"><div id="name_code"><?php echo '<input type="hidden" name="chatroom" id="chatroom" value="'. $this->chatrooms[0]. '" />'; if($this->chatadd === 1) { echo $this->lsite['addnmcd'].': &nbsp; <em id="code_ch">'. substr(md5(date(" j-F-Y, g:i a ")), 3, 4). '</em><br />'. $this->lsite['name'].': <input type="text" name="chatuser" id="chatuser" size="12" maxlength="10"/> '. $this->lsite['code'].': <input type="text" name="cod" id="cod" size="4" maxlength="4" /> &nbsp; <input type="button" name="set" id="set" value="Set" onclick="setNameC(this.form)" />'; } else if(defined('CHATUSER')) { echo '<input type="hidden" name="chatuser" id="chatuser" value="'. CHATUSER. '" /><span id="enterchat" onclick="enterChat()">'.sprintf($this->lsite['enterchat'], CHATUSER).'</span>';} $alink = (CHATLINK === 1) ? '<img src="chatex/url.png" alt="URL" width="16" height="16" onclick="setUrl(\'adchat\');" />' : ''; ?></div><div id="chatadd"><div id="chatex"><img src="chatex/bold.png" alt="B" width="16" height="16" onclick="addChatBIU('[b]','[/b]', 'adchat');" /> &nbsp; <img src="chatex/italic.png" alt="I" width="16" height="16" onclick="addChatBIU('[i]','[/i]', 'adchat');" /> &nbsp; <img src="chatex/underline.png" alt="U" width="16" height="16" onclick="addChatBIU('[u]','[/u]', 'adchat');" /> &nbsp; <?php echo $alink; ?> &nbsp; <img src="chatex/0.gif" alt=":)" title=":)" width="18" height="18" onclick="addSmile(':)', 'adchat');"/> &nbsp; <img src="chatex/1.gif" alt=":(" title=":(" width="18" height="18" onclick="addSmile(':(', 'adchat');"/> &nbsp; <img src="chatex/2.gif" alt=":P" title=":P" width="18" height="18" onclick="addSmile(':P', 'adchat');"/> &nbsp; <img src="chatex/3.gif" alt=":D" title=":D" width="18" height="18" onclick="addSmile(':D', 'adchat');"/> &nbsp; <img src="chatex/4.gif" alt=":S" title=":S" width="18" height="18" onclick="addSmile(':S', 'adchat');"/> &nbsp; <img src="chatex/5.gif" alt=":O" title=":O" width="18" height="18" onclick="addSmile(':O', 'adchat');"/> &nbsp; <img src="chatex/6.gif" alt=":=)" title=":=)" width="18" height="18" onclick="addSmile(':=)', 'adchat');"/> &nbsp; <img src="chatex/7.gif" alt=":|H" title=":|H" width="42" height="18" onclick="addSmile(':|H', 'adchat');"/> &nbsp; <img src="chatex/8.gif" alt=":X" title=":X" width="18" height="18" onclick="addSmile(':X', 'adchat');"/> &nbsp; <img src="chatex/9.gif" alt=":-*" title=":-*" width="18" height="18" onclick="addSmile(':-*', 'adchat');"/></div><input type="text" name="adchat" id="adchat" size="88" maxlength="200"/> &nbsp; <input type="submit" value="<?php echo $this->lsite['chat']; ?>" id="submit"/> &nbsp; <input type="button" name="logout" id="logout" value="Logout" onclick="delCookie('name_c')"/></div></form>